sys_script_include.script:
var GetNeeditDetails = Class.create();
GetNeeditDetails.prototype = Object.extendsObject(AbstractAjaxProcessor, {
    getDetails: function() {
        var recordId = this.getParameter('sysparm_record_id');
        var result = {};

        var gr = new GlideRecord('x_58872_needit_needit');
        if (gr.get(recordId)) { // Fetch the record by ID
            result.hotcut_disco = gr.getValue('hotcut_disco');
            result.reason_type = gr.getValue('reason_type');
            result.reason_code = gr.getValue('reason_code');

        }

        return JSON.stringify(result); // Return the result as a JSON string
    }
});
