sys_script_include.script:
var YourScriptInclude = Class.create();
YourScriptInclude.prototype = {
    initialize: function() {
    },

    getDetails: function() {
        var result = {};
        // Example: Fetching data from 'example_table'
        var gr = new GlideRecord('x_58872_needit_needit');
        gr.query();
        if (gr.next()) {
            result.hotcut_disco = gr.getValue('hotcut_disco');
            result.reason_type = gr.getValue('reason_type');
            result.reason_code = gr.getValue('reason_code');
        }
        return JSON.stringify(result);
    },

    type: 'YourScriptInclude'
};
